func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		queryGetQuerySpecification, err := honeycomb.GetQuerySpecification(ctx, &honeycomb.GetQuerySpecificationArgs{
			Calculations: []honeycomb.GetQuerySpecificationCalculation{
				{
					Op:     "P99",
					Column: pulumi.StringRef("duration_ms"),
				},
			},
			Filters: []honeycomb.GetQuerySpecificationFilter{
				{
					Column: "trace.parent_id",
					Op:     "does-not-exist",
				},
			},
			Breakdowns: []string{
				"app.tenant",
			},
		}, nil)
		if err != nil {
			return err
		}
		queryQuery, err := honeycomb.NewQuery(ctx, "queryQuery", &honeycomb.QueryArgs{
			Dataset:   pulumi.Any(_var.Dataset),
			QueryJson: pulumi.String(queryGetQuerySpecification.Json),
		})
		if err != nil {
			return err
		}
		_, err = honeycomb.NewBoard(ctx, "board", &honeycomb.BoardArgs{
			Queries: honeycomb.BoardQueryArray{
				&honeycomb.BoardQueryArgs{
					QueryId: queryQuery.ID(),
				},
			},
		})
		if err != nil {
			return err
		}
		return nil
	})
}
